import React from 'react';

function Logged(){
    let [loginMsg, setLoginMsg] = React.useState(<p>Teste</p>)
    
    async function get() {
        const response = await fetch('/api/logincookie', {
            method: "GET",
            headers: {
                'Content-type': 'application/json'
            }
        })
        const data = await response.json()
        console.log(data)
        setLoginMsg(data.username)
    }
    console.log("tem q ser 1 vez");
    function Render(){
        return loginMsg
    }
    return(
        <div>
            <Render/>
        </div>
    )
}

export default Logged
