import React from 'react';

function Logged(){
    let [loginMsg, setLoginMsg] = React.useState('')
    async function get() {
        const response = await fetch('/api/logincookie', {
            method: "GET",
            headers: {
                'Content-type': 'application/json'
            }
        })
        const data = await response.json()
        setLoginMsg(data.username)
    }
    get()

    return (
        <p>Ol√° {loginMsg}</p>
    )
}
