import React from 'react';

function Logged(){
    let [loginMsg, setLoginMsg] = React.useState('')
    
    async function get() {
        const response = await fetch('/api/logincookie', {
            method: "GET",
            headers: {
                'Content-type': 'application/json'
            }
        })
        const data = await response.json()
        console.log(data)
        setLoginMsg(data.username)
    }
    console.log("tem q ser 1 vez");
    function Render(){
        console.log("Fodase");
        get().then(() => {
            return (
                <p>Ol√° {loginMsg}</p>
            )
        })
    }
    return(
        <div>
            <p>test</p>
            <Render></Render>
        </div>
    )
}

export default Logged
